program.configure:
case "${SB_SYSTYPE}" in
*-aix4*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"

  # We do not have stpcpy()
  export ac_cv_func_stpcpy=no ;;
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -std" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae +Onofltacc +ESlit +DAportable \
+Oentrysched +Odataprefetch" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -O2 -OPT:Olimit=0" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;;
esac

rm -f config.cache
CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" ./configure \
--datadir="${_datadir}" --prefix=${SB_INSTALL_PREFIX}

program.install:
gmake install

# copy texinfo files
ginstall -m 755 -o root -g 0 -d ${_docdir}
ginstall -m 644 -o root -g 0 doc/bison.texinfo doc/version.texi ${_docdir}
