program.script-header:
case "${SB_SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"
  LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib:/lib"
  CC_LD_RT="-Wl,-blibpath:" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae +ESlit +Onofltacc +DAportable \
+Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s -Wl,+b," ;;
*-irix6*)
  CC=cc CFLAGS="-O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;;
*-freebsd*|*-linux*)
  CC_LD_RT="-Wl,-rpath," ;;
*-osf4*)
  CC=cc CFLAGS="-O2 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath,"

  # don't use inttypes.h
  export ac_cv_header_inttypes_h=no ;;
*-osf*)
  CC=cc CFLAGS="-O2 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8"
  CC_LD_RT="-R" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic"
  CC_LD_RT="-R" ;;
esac
