program.configure:
case "${SB_SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae -z +ESlit +Onofltacc +DAportable \
+Oentrysched +Odataprefetch +Onolimit" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -msym -readonly_strings" ;;
*-solaris2.[89])
#  CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic -xarch=v8" ;;
   CC=gcc  ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" ;;
esac

./configure ac_cv_path_tclsh=${SB_VAR_TCL}/bin/tclsh8.4 \
CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" --disable-dependency-tracking \
--prefix=${SB_INSTALL_PREFIX}

program.install:
gmake pkgdatadir="${_datadir}" install

${SB_PATH_GINSTALL} -m 755 -o root -g 0 -d ${SB_INSTALL_PREFIX}/info
${SB_VAR_TEXINFO}/bin/makeinfo --no-split \
-o ${SB_INSTALL_PREFIX}/info/dejagnu.info doc/dejagnu.texi

${SB_PATH_GINSTALL} -m 755 -o root -g 0 -d ${_docdir}
${SB_PATH_GINSTALL} -m 644 -o root -g 0 doc/dejagnu.texi ${_docdir}
