program.configure:
# IRIX has gdiff as some old GUI diff program (now xdiff)
case "${SB_SYSTYPE}" in
*-aix4*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"

  # We do not want to -D_LINUX_SOURCE_COMPAT for stpcpy()
  export ac_cv_func_stpcpy=no ;;
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -std" ;;
*-hpux11*)
  CC=cc CFLAGS="+O2 -Ae +ESlit +Onofltacc +DAportable \
+Oentrysched +Odataprefetch +Onolimit" ;;
*-hpux10*)
  # C compiler causes gnudiff to coredump so use GCC
  CFLAGS="-O2 -march=1.1" ;;
*-irix*)
  CC=cc CFLAGS="-O2 -OPT:Olimit=0" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" ;;
esac

CC="${CC:=gcc}" CFLAGS="${CFLAGS:=-O2}" ./configure \
--program-prefix=gnu --with-included-gettext \
--disable-dependency-tracking --with-default-diff-program=gnudiff \
--datadir="${_datadir}" --prefix=${SB_INSTALL_PREFIX}
