program.configure:
case "${SB_SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"
  LDFLAGS="-Wl,-brtl" ARGS="--enable-threads" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -std1" ARGS="--enable-threads" ;;
*-hpux10*)
  CC=cc CFLAGS="+O2 -Ae -z +Onofltacc +ESlit +DAportable \
+Oentrysched +Odataprefetch +Onolimit"
  ARGS="--with-threads=none" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae -z +Onofltacc +ESlit +DAportable \
+Oentrysched +Odataprefetch +Onolimit"
  ARGS="--enable-threads" ;;
*-irix6*)
  CC=cc CFLAGS="-O2 -OPT:Olimit=0" ARGS="--enable-threads" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8"
  ARGS="--enable-threads" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic"
  ARGS="--enable-threads";;
esac

CC="${CC:-gcc}" CFLAGS="${CFLAGS:=-O2}" \
LDFLAGS="${LDFLAGS+${LDFLAGS}}" ./configure \
--datadir="${_datadir}" ${ARGS+${ARGS}} --prefix=${SB_INSTALL_PREFIX}

program.install:
gmake install

# install texinfo source files
${SB_PATH_GINSTALL} -m 755 -o root -g 0 -d ${_docdir}
${SB_PATH_GINSTALL} -m 644 -o root -g 0 docs/glib.texi ${_docdir}


case "${SB_SYSTYPE}" in
*-aix*)
  CC_LD_RT="-Wl,-blibpath:" LDFLAGS=":/usr/lib" ;;
*-osf*|*-irix6*|*-linux*)
  CC_LD_RT="-Wl,-rpath," ;;
*-hpux*)
  CC_LD_RT="-Wl,+s,+b," ;;
*-solaris2*)   
  CC_LD_RT="-R" ;;
esac

# quote \@ for perl
_p_install_prefix=$(echo ${SB_INSTALL_PREFIX} | sed 's/@/\\@/')

${SB_VAR_PERL}/bin/perl -pi -e "\
s!-bexpall!-Wl,-bexpall!; \
s!-L(\\\${exec_prefix}/lib) !-L\$1 ${CC_LD_RT}\$1 !;" \
${SB_INSTALL_PREFIX}/bin/glib-config
