program.script-header:
_datadir="${SB_INSTALL_PREFIX}/share"
_docdir="${SB_INSTALL_PREFIX}/doc"
_sysconfdir="${SB_INSTALL_PREFIX}/etc"

case "${SB_SYSTYPE}" in
*-aix*)
  CC_LD_RT="-Wl,-blibpath:" ;;
*-hpux*)
  CC_LD_RT="-Wl,+s,+b," ;;
*-irix*|*-linux*|*-osf*)
  CC_LD_RT="-Wl,-rpath," ;;
*-solaris*)
  CC_LD_RT="-R" ;;
esac

program.uninstall:
# remove entry for scrollkeeper DTD
${SB_VAR_LIBXML}/bin/xmlcatalog --noout --del \
  ${_datadir}/scrollkeeper-omf.dtd ${SB_VAR_XMLCATALOG}

rm -rf ${SB_INSTALL_PREFIX}

module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" ;; *-hpux*) CC=cc CFLAGS="+O2 -z +ESlit +Onofltacc +DAportable \ +Oentrysched +Odataprefetch +Onolimit" ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 \ -OPT:Olimit=0" ;; *-osf*) CC=cc CFLAGS="-O2 -msym -readonly_strings" ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" ;; esac
./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="-I${SB_VAR_LIBPOPT_INC} -I${SB_VAR_GETTEXT_INC} \ -I${SB_VAR_JPEG_INC}" LDFLAGS="-L${SB_VAR_LIBPOPT_LIB} \ -L${SB_VAR_GETTEXT_LIB} -L${SB_VAR_JPEG_LIB} \ -L${SB_VAR_FCPACKAGE_LIB} ${CC_LD_RT}${SB_VAR_LIBPOPT_LIB}:\ ${SB_VAR_JPEG_LIB}${SB_VAR_LIBOPENSSL+:${SB_VAR_LIBOPENSSL_LIB}} \ ${LDFLAGS+${LDFLAGS}}" PKG_CONFIG_PATH="${SB_VAR_LIBGLIB_LIB}/pkgconfig:\ ${SB_VAR_LIBXML_LIB}/pkgconfig:${SB_VAR_LIBATK_LIB}/pkgconfig:\ ${SB_VAR_LIBPANGO_LIB}/pkgconfig:${SB_VAR_LIBGTK_LIB}/pkgconfig:\ ${SB_VAR_FCPACKAGE_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_VAR_LIBGLADE_LIB}/pkgconfig:${SB_INSTALL_PREFIX}/lib/pkgconfig:\ ${SB_VAR_LIBART_LIB}/pkgconfig:${SB_VAR_ESOUND_LIB}/pkgconfig:\ ${SB_VAR_LIBAUDIOFILE_LIB}/pkgconfig:${SB_VAR_GETTEXT_LIB}/pkgconfig\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}" \ --datadir="${_datadir}" --sysconfdir="${_sysconfdir}" \ --with-gconf-schema-file-dir="${_sysconfdir}/gconf/schemas" \ --disable-dependency-tracking --enable-shared --enable-static \ --prefix=${SB_INSTALL_PREFIX}
for _pc in libnautilus/libnautilus.pc; do ${SB_PATH_SHTOOL} subst -q -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!" ${_pc} done

case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" ARGS="--disable-ipv6" ;; *-hpux*) CC=cc CFLAGS="+O2 -z +ESlit +Onofltacc +DAportable \ +Oentrysched +Odataprefetch +Onolimit" ARGS="--disable-ipv6" ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 -OPT:Olimit=0" ARGS="--disable-ipv6" ;; *-linux9*|*-linuxe3*) CPPFLAGS="-DOPENSSL_NO_KRB5" ARGS="--enable-ipv6" ;; *-linux*) CFLAGS="-O1" ARGS="--enable-ipv6" ;; *-osf*) CC=cc CFLAGS="-O2 -msym -readonly_strings" ARGS="--disable-ipv6" ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" ARGS="--enable-ipv6" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" ARGS="--disable-ipv6" ;; esac
./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="-I${SB_VAR_LIBPOPT_INC} -I${SB_VAR_GETTEXT_INC} \ -I${SB_VAR_BZIP2_INC} -I${SB_VAR_ZLIB_INC} \ ${CPPFLAGS+${CPPFLAGS}}" LDFLAGS="-L${SB_VAR_LIBPOPT_LIB} \ -L${SB_VAR_GETTEXT_LIB} -L${SB_VAR_BZIP2_LIB} -L${SB_VAR_ZLIB_LIB} \ ${SB_VAR_LIBOPENSSL+-L${SB_VAR_LIBOPENSSL_LIB}} \ ${CC_LD_RT}${SB_VAR_LIBPOPT_LIB}:${SB_VAR_BZIP2_LIB}:\ ${SB_VAR_ZLIB_LIB}${SB_VAR_LIBOPENSSL+:${SB_VAR_LIBOPENSSL_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="${SB_VAR_LIBGLIB_LIB}/pkgconfig:\ ${SB_VAR_LIBGTK_LIB}/pkgconfig:${SB_VAR_LIBXML_LIB}/pkgconfig:\ ${SB_VAR_LIBPANGO_LIB}/pkgconfig:${SB_VAR_LIBATK_LIB}/pkgconfig:\ ${SB_VAR_FCPACKAGE_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_INSTALL_PREFIX}/lib/pkgconfig:${SB_VAR_GETTEXT_LIB}/pkgconfig\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}\ ${SB_VAR_LIBOPENSSL+:${SB_VAR_LIBOPENSSL_LIB}/pkgconfig}" \ --datadir="${_datadir}" --with-html-dir="${_docdir}/html" \ --disable-samba --sysconfdir="${_sysconfdir}" \ --with-gconf-schema-file-dir="${_sysconfdir}/gconf/schemas" \ --disable-gtk-doc --enable-shared --enable-static \ --disable-dependency-tracking --prefix=${SB_INSTALL_PREFIX}
