program.configure:
case "${SB_SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"
  LDFLAGS="-Wl,-brtl" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae -z +Onofltacc +ESlit +DAportable \
+Oentrysched +Odataprefetch +Onolimit" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 \
-OPT:Olimit=0" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -msym -readonly_strings" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;;
esac

rm -f config.cache
CC="${CC:=gcc}" CFLAGS="${CFLAGS:=-O2}" \
LDFLAGS="${LDFLAGS+${LDFLAGS}}" ./configure --enable-shared \
--enable-static --with-x --prefix=${SB_INSTALL_PREFIX}

program.install:
gmake install

ginstall -m 755 -o root -g 0 -d ${_docdir}
ginstall -m 644 -o root -g 0  doc/*.html doc/*.png doc/*.txt ${_docdir}

sed "1 s!/usr/bin/perl!${PERL}/bin/perl!" \
${SB_INSTALL_PREFIX}/bin/gifburst >/tmp/$$
cp /tmp/$$ ${SB_INSTALL_PREFIX}/bin/gifburst; rm -f /tmp/$$
