program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" CC_LD_RT="-Wl,-blibpath:" LDFLAGS="-Wl,-brtl" ;; *-hpux*) CC="cc -Ae" CFLAGS="+O2 -z +ESlit +Onofltacc +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 \ -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ARGS="--with-xkb-base=/usr/lib/X11/xkb" ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-msym -O2 -readonly_strings" CC_LD_RT="-Wl,-rpath," ARGS="--with-xkb-base=/usr/lib/X11/xkb" ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" CC_LD_RT="-R" ARGS="--with-xkb-base=/usr/openwin/lib/X11/xkb" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" CC_LD_RT="-R" ;; esac
./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ LDFLAGS="${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="${SB_VAR_LIBXML_LIB}/pkgconfig" \ ${SB_VAR_LIBICONV+--with-libiconv-prefix=${SB_VAR_LIBICONV_LIB}} \ --disable-dependency-tracking ${ARGS+${ARGS}} \ --prefix=${SB_INSTALL_PREFIX}
for _pc in libxklavier.pc; do ${SB_PATH_SHTOOL} subst -q -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" ${_pc} done
