program.configure:
case "${SB_SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-osf*)
  # doesn't build on Tru64 UNIX 4.0D:
  #   http://savannah.gnu.org/bugs/?func=detailbug&bug_id=2502&group_id=71
  CC=cc CFLAGS="-Wl,-msym -O2 -readonly_strings" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae +Onofltacc +ESlit +DAportable \
+Oentrysched +Odataprefetch +Onolimit" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 -OPT:Olimit=0" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;;
esac

./configure CC="${CC:=gcc}" CFLAGS="${CFLAGS=-O2}" \
--datadir="${_datadir}" --program-prefix=g \
${LIBICONV+--with-libiconv-prefix=${LIBICONV}} \
${GETTEXT+--with-libintl-prefix=${GETTEXT}} \
--disable-dependency-tracking --prefix=${SB_INSTALL_PREFIX}

program.install:
make install

ginstall -m 755 -o root -g 0 -d ${_docdir}
ginstall -m 644 -o root -g 0 doc/fdl.texi doc/make.texi \
doc/make-stds.texi ${_docdir}


# setgid unnecessary
case "${SB_SYSTYPE}" in
*-linux*)
  chgrp 0 ${SB_INSTALL_PREFIX}/bin/gmake
  chmod g-s ${SB_INSTALL_PREFIX}/bin/gmake ;;
esac
