program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" CC_LD_RT="-Wl,-blibpath:" LDFLAGS="-Wl,-brtl" ;; *-hpux*) CC=cc CFLAGS="+O2 -Ae -z +ESlit +Onofltacc +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 \ -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -std1" CC_LD_RT="-Wl,-rpath," ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" CC_LD_RT="-R" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" CC_LD_RT="-R" ;; esac
./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ LDFLAGS="${LDFLAGS+${LDFLAGS}}" --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SHTOOL} subst -q -e "\ s!-L\(\$mm_libdir\)!-L\1 ${CC_LD_RT}\1!;" mm-config
