module.configure:
case "${SB_SYSTYPE}" in *-aix5*) CC=xlc CFLAGS="-O -qmaxmem=-1 -qarch=com" CXX=xlC CXXFLAGS="-O -qmaxmem=-1 -qarch=com" LDFLAGS="-brtl -blibpath:/usr/lib" CC_LD_RT="-blibpath:" ARGS="--enable-ipv6=no" ;; *-hpux11*) CC=cc CFLAGS="+O1 -Ae +ESlit +DAportable +Oentrysched" CXX=aCC CXXFLAGS="+O1 -AA +ESlit +DAportable +Oentrysched" LDFLAGS="-Wl,+cdp,../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/" \ CC_LD_RT="-Wl,+s,+b," ARGS="--enable-ipv6=no" ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -mips4 -r5000 -O1 -OPT:Olimit=0" CXX=CC CXXFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -mips4 -r5000 -O1 -OPT:Olimit=0 \ -LANG:std=off:libc_in_namespace_std=off" CC_LD_RT="-Wl,-rpath,"
# for for transitive linking LDFLAGS="-L${SB_VAR_GETTEXT_LIB} -L${SB_VAR_LIBEXPAT_LIB} \ -L${SB_VAR_LIBICONV_LIB}" ;; *-linux*) CC_LD_RT="-Wl,-rpath," ARGS="--enable-xinerama \ --enable-old-abi-compat-wrappers" LDFLAGS="-L${SB_VAR_FCPACKAGE_LIB}" ;; *-osf*) CC=cc CFLAGS="" CXX=cxx CXXFLAGS="" CC_LD_RT="-Wl,-rpath," ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" CXXFLAGS="-O2 -mcpu=v8" CC_LD_RT="-R" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" CXX=CC CXXFLAGS="-xO2 -xtarget=generic" CC_LD_RT="-R" ;; esac
rm -f config.cache CC="${CC:-gcc}" CXX="${CXX:-g++}" \ CFLAGS="${CFLAGS:--O2}" XCFLAGS="${CFLAGS:--O2}" \ CXXFLAGS="${CXXFLAGS:--O2}" \ LDFLAGS="${CC_LD_RT}${SB_INSTALL_PREFIX}/lib:${SB_VAR_LIBMNG_LIB}:\ ${SB_VAR_LIBPNG_LIB}:${SB_VAR_JPEG_LIB}:${SB_VAR_ZLIB_LIB}\ ${SB_VAR_GCC_RT+:${SB_VAR_GCC_RT_LIB}} ${LDFLAGS+${LDFLAGS}}" \ PERL=${SB_VAR_PERL}/bin/perl \ PKG_CONFIG_PATH="${SB_VAR_FCPACKAGE_LIB}/pkgconfig:\ ${SB_VAR_LIBTTF_LIB}/pkgconfig" \ ./configure --with-system-jpeg=${SB_VAR_JPEG} \ --with-system-zlib=${SB_VAR_ZLIB} --with-system-png=${SB_VAR_LIBPNG} \ --with-system-mng=${SB_VAR_LIBMNG} --enable-toolkit-gtk \ --with-gtk-prefix=${SB_VAR_GTK} --with-glib-prefix=${SB_VAR_GLIB} \ --with-ft-prefix=${SB_VAR_LIBTTF} --enable-crypto \ --enable-ldap-experimental --disable-xprint \ --disable-strip-libs --enable-nspr-autoconf \ --enable-extensions=default,irc --disable-debug \ --enable-xft --datadir="${_datadir}" \ --with-default-mozilla-five-home="${SB_INSTALL_PREFIX}/lib" \ ${ARGS+${ARGS}} --prefix=${SB_INSTALL_PREFIX}

case "${SB_SYSTYPE}" in *-aix5*) CC=xlc CFLAGS="-O1 -qmaxmem=-1 -qarch=com" CXX=xlC CXXFLAGS="-O1 -qmaxmem=-1 -qarch=com" LDFLAGS="-brtl -blibpath:/usr/lib" CC_LD_RT="-blibpath:" ARGS="--enable-ipv6=no" ;; *-hpux11*) CC=cc CFLAGS="+O1 -Ae +ESlit +DAportable +Oentrysched" CXX=aCC CXXFLAGS="+O1 -AA +ESlit +DAportable +Oentrysched" LDFLAGS="-Wl,+cdp,../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/ \ -Wl,+cdp,../../../../../../dist/bin/:${SB_INSTALL_PREFIX}/lib/" \ CC_LD_RT="-Wl,+s,+b," ARGS="--enable-ipv6=no" ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -mips4 -r5000 -O1 -OPT:Olimit=0" CXX=CC CXXFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -mips4 -r5000 -O1 -OPT:Olimit=0 \ -LANG:std=off:libc_in_namespace_std=off" CC_LD_RT="-Wl,-rpath,"
# for for transitive linking LDFLAGS="-L${SB_VAR_GETTEXT_LIB} -L${SB_VAR_LIBEXPAT_LIB} \ -L${SB_VAR_LIBICONV_LIB}" ;; *-linux*) CC_LD_RT="-Wl,-rpath," ARGS="--enable-xinerama \ --enable-old-abi-compat-wrappers" LDFLAGS="-L${SB_VAR_FCPACKAGE_LIB}" ;; *-osf*) CC=cc CFLAGS="-O2 -msym -readonly_strings" CXX=cxx CXXFLAGS="-O2 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" CXXFLAGS="-O2 -mcpu=v8" CC_LD_RT="-R" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" CXX=CC CXXFLAGS="-xO2 -xtarget=generic" CC_LD_RT="-R" ;; esac
rm -f config.cache CC="${CC:-gcc}" CXX="${CXX:-g++}" \ CFLAGS="${CFLAGS:--O2}" XCFLAGS="${CFLAGS:--O2}" \ CXXFLAGS="${CXXFLAGS:--O2}" \ LDFLAGS="${CC_LD_RT}${SB_INSTALL_PREFIX}/lib:${SB_VAR_LIBMNG_LIB}:\ ${SB_VAR_LIBPNG_LIB}:${SB_VAR_JPEG_LIB}:${SB_VAR_ZLIB_LIB}\ ${SB_VAR_GCC_RT+:${SB_VAR_GCC_RT_LIB}} ${LDFLAGS+${LDFLAGS}}" \ PERL=${SB_VAR_PERL}/bin/perl \ PKG_CONFIG_PATH="${SB_VAR_FCPACKAGE_LIB}/pkgconfig:\ ${SB_VAR_LIBGLIB_LIB}/pkgconfig:${SB_VAR_LIBGTK_LIB}/pkgconfig:\ ${SB_VAR_LIBPANGO_LIB}/pkgconfig:${SB_VAR_LIBATK_LIB}/pkgconfig:\ ${SB_VAR_LIBIDL_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}" \ ./configure --with-system-jpeg=${SB_VAR_JPEG} \ --with-system-zlib=${SB_VAR_ZLIB} --with-system-png=${SB_VAR_LIBPNG} \ --with-system-mng=${SB_VAR_LIBMNG} --enable-default-toolkit=gtk2 \ --with-ft-prefix=${SB_VAR_LIBTTF} --enable-crypto \ --disable-ldap --disable-mailnews --disable-strip-libs \ --enable-nspr-autoconf --enable-extensions=default,irc \ --disable-debug --enable-xft --datadir="${_datadir}" \ --with-default-mozilla-five-home="${SB_INSTALL_PREFIX}/lib" \ ${ARGS+${ARGS}} --prefix=${SB_INSTALL_PREFIX}
