program.install:
gmake install
# install DSSSL catalog _dsssl_datadir="${_dsssl_datadir}/docbook/dsssl-openjade" ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_dsssl_datadir}
for _file in builtins.dsl catalog dsssl.dtd fot.dtd style-sheet.dtd; do ${SB_PATH_INSTALL} -m 644 -o root -g 0 dsssl/${_file} ${_dsssl_datadir} done
# install SGML Docbook catalog entry ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --sgml --no-super-update \ --add ${_sgml_catalog} ${_docbook_sgml_catalog}
# install DSSSL catalog entries ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --sgml --no-super-update \ --add ${_docbook_sgml_catalog} ${_dsssl_datadir}/catalog

program.uninstall:
_dsssl_datadir="${_dsssl_datadir}/docbook/dsssl-openjade"

rm -rf ${_dsssl_datadir}

rmdir ${_dsssl_datadir}/docbook 2>/dev/null

# remove DSSSL catalog entries
${SB_VAR_LIBXML}/bin/xmlcatalog --noout --sgml --no-super-update \
--del ${_docbook_sgml_catalog} ${_dsssl_datadir}/catalog

# remove SGML Docbook catalog entry
${SB_PATH_SB} -q module-names -i xmlcatalog-1.1 2>/dev/null |\
${SB_PATH_EGREP} 'docbook-dsssl|docbook-sgml' >/dev/null || {
  ${SB_VAR_LIBXML}/bin/xmlcatalog --noout --sgml --no-super-update \
  --del ${_sgml_catalog} ${_docbook_sgml_catalog}
}

rm -rf ${SB_INSTALL_PREFIX}
