program.build:
case "${SB_SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -std1" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae +DAportable +Oentrysched +Odataprefetch +Onolimit" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -woff 1429 -O2 -OPT:Olimit=0" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;;
esac

gmake -f Makefile.unix BINDIR=${SB_INSTALL_PREFIX}/bin \
PERL=${PERL}/bin/perl MANDIR=${SB_INSTALL_PREFIX}/man \
CC="${CC:-gcc}" INCLUDEDIR="${_datadir}" \
PAPER=letter \
CFLAGS="-DPAPER=\\\"letter\\\" \
-DUNIX ${CFLAGS:--O2}"
