module.configure:
case "${SYSTYPE}" in
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" LDFLAGS="-Wl,-brtl" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -ieee -std1" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae -z +ESlit +Onofltacc +DAportable \
+Oentrysched +Odataprefetch +Onolimit"
  ARGS="--with-curses" ;;
*-irix6*)
  CC=cc CFLAGS="-O2 -OPT:Olimit=0" ;;
*-sun-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;;
esac

rm -f config.cache
./configure CC="${CC:=gcc}" CFLAGS="${CFLAGS:--O2}" \
${LDFLAGS+LDFLAGS="${LDFLAGS}"} --enable-static --enable-shared \
${ARGS+${ARGS}} --prefix=${INSTALL_PREFIX}

module.install:
gmake install

ginstall -m 755 -o root -g 0 -d ${_docdir}

# install texinfo source files
ginstall -m 644 -o root -g 0 doc/hist.texinfo doc/rlman.texinfo \
doc/manvers.texinfo doc/hsuser.texinfo doc/hstech.texinfo \
doc/rluser.texinfo doc/rluserman.texinfo doc/rltech.texinfo ${_docdir}

