program.configure:
case "${SB_SYSTYPE}" in
*-aix4*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"

  # We do not want to -D_LINUX_SOURCE_COMPAT for stpcpy()
  export ac_cv_func_stpcpy=no ;;
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -std1" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae -z +ESlit +Onofltacc +DAportable \
+Oentrysched +Odataprefetch +Onolimit" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=generic" ;;
esac

# don't install uuencode or uudecode
rm -f config.cache
CC="${CC:-gcc}" CPPFLAGS="-I${GETTEXT}/include" \
CFLAGS="${CFLAGS:--O2}" PERL=${PERL}/bin/perl \
DIFF=${DIFFUTILS}/bin/gnudiff LDFLAGS="-L${GETTEXT}" ./configure \
--disable-uucode --with-gnu-gettext --program-prefix=g \
--datadir="${_datadir}" ${ARGS+${ARGS}} --prefix=${SB_INSTALL_PREFIX}

program.install:
gmake install

# install texinfo source files
ginstall -m 755 -o root -g 0 -d ${_docdir}
ginstall -m 644 -o root -g 0 doc/remsync.texi doc/sharutils.texi \
doc/version.texi ${_docdir}
