program.configure:
case "${SB_SYSTYPE}" in
*-aix4*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com"

  # We do not want to -D_LINUX_SOURCE_COMPAT for stpcpy()
  # and __linux_strerror_r()
  export ac_cv_func___linux_strerror_r=no;
  export ac_cv_func_stpcpy=no ;;
*-aix*)
  CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;;
*-osf*)
  CC=cc CFLAGS="-O2 -nodtk -std" ;;
*-hpux*)
  CC=cc CFLAGS="+O2 -Ae -z +ESlit +Onofltacc +DAportable \
+Oentrysched +Odataprefetch +Onolimit" ;;
*-irix6*)
  CC=cc CFLAGS="-Wl,-woff,84 -woff 1429 -O2 -OPT:Olimit=0" ;;
*-solaris2.[89])
  CFLAGS="-O2 -mcpu=v8" ;;
*-solaris2*)
  CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;;
esac

rm -f config.cache
./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \
--program-prefix=g --with-included-gettext \
--disable-dependency-tracking --datadir="${_datadir}" \
--prefix=${SB_INSTALL_PREFIX}

program.install:
gmake install

ginstall -m 755 -o root -g 0 -d ${_docdir}
ginstall -m 644 -o root -g 0 doc/fdl.texi doc/freemanuals.texi \
doc/getdate.texi doc/header.texi doc/tar.texi doc/version.texi ${_docdir}
