program.configure:
case "${SB_SYSTYPE}" in *-aix4*) CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" LDFLAGS="-Wl,-brtl"
# We do not want to -D_LINUX_SOURCE_COMPAT for stpcpy() export ac_cv_func_stpcpy=no ;; *-aix*) CC=xlc CFLAGS="-O2 -qmaxmem=-1 -qarch=com" ;; *-osf*) CC=cc CFLAGS="-O2 -std1 -readonly_strings" ;; *-hpux*) CC=cc CFLAGS="-Ae +O2 -z +ESlit +Onofltacc +DAportable \ +Oentrysched +Odataprefetch +Onolimit" ;; *-irix6*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -O2 \ -OPT:Olimit=0" ;; *-solaris2.[89]) CFLAGS="-O2 -mcpu=v8" ;; *-solaris2*) CC=cc CFLAGS="-mr -Qn -xO2 -xtarget=generic" ;; esac
rm -f config.cache ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ ${LIBICONV+--with-libiconv-prefix=${LIBICONV}} \ --with-libintl-prefix=${GETTEXT} \ --datadir="${_datadir}" --disable-dependency-tracking \ --prefix=${SB_INSTALL_PREFIX}
