program.install:
${SB_VAR_PYTHON}/bin/python setup.py install -O1 --skip-build \
--install-lib=${_install_prefix} --prefix=${SB_INSTALL_PREFIX}

# install path for Python's include.paths.d
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \
${_install_prefix}/include.paths.d
cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_
${_install_prefix}
_EOT_

${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${SB_INSTALL_PREFIX}/bin
(cd ${SB_INSTALL_PREFIX}/bin
 rm -f bookify tap2rpm tap2deb
 for _bin in *; do
   ${SB_PATH_SED} -i -e "\
s!@pycryptodir@!${_pycryptodir}!;
s!@libdir@!${_install_prefix}!;" ${_bin}
 done)

# install man pages
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \
${SB_INSTALL_PREFIX}/man/man1
(for _man in */doc/man/*.1; do
   _base=$(echo ${_man} | sed 's,.*/,,')
   case "${_base}" in
     pyhtmlizer.1|t-im.1|tap2deb.1|tap2rpm.1) continue ;;
   esac
   ${SB_PATH_INSTALL} -m 644 -o root -g 0 ${_man} \
   ${SB_INSTALL_PREFIX}/man/man1/${_base}
 done)

# install documentation
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_docdir}
(cd TwistedCore-2.5.0/doc
 find . | cpio -pdmu ${_docdir}
 rm -rf ${_docdir}/man)
${SB_PATH_CHOWN} -hR 0:0 ${_docdir}
${SB_PATH_FIND} ${_docdir} -type d -exec ${SB_PATH_CHMOD} 755 {} \;
${SB_PATH_FIND} ${_docdir} -type f -exec ${SB_PATH_CHMOD} 644 {} \;
