program.script-header:
_docdir="${SB_INSTALL_PREFIX}/doc"
_docsubdir="doc"
_sysconfdir="${SB_INSTALL_PREFIX}/etc"
_sysconfsubdir="etc"

case "${SB_SYSTYPE}" in
*-aix*)
  export JAVA_HOME=/usr/java5 ;;
*-hpux*)
  export JAVA_HOME=/opt/java1.5 ;;
*-linux*)
  export JAVA_HOME=/opt/java1.5 ;;
*-solaris*)
  export JAVA_HOME=/opt/java1.5 ;;
esac

export ANT_HOME=${SB_INSTALL_PREFIX}
PATH="${JAVA_HOME}/bin:${PATH}"
