module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc_r CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CPPFLAGS="-I${SB_VAR_GETTEXT_INC} \ ${SB_VAR_LIBICONV+-I${SB_VAR_LIBICONV_INC}}" \ CFLAGS="${CFLAGS:=-O2}" LDFLAGS="-L${SB_VAR_GETTEXT_LIB} \ ${SB_VAR_LIBICONV+-L${SB_VAR_LIBICONV_LIB}} ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="${SB_VAR_LIBGLIB}/lib/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}\ ${SB_VAR_GTKDOC+:${SB_VAR_GTKDOC}/lib/pkgconfig}" \ --disable-gtk-doc --enable-static --enable-shared \ --datarootdir=${_datadir} --datadir=${_datadir} \ --disable-dependency-tracking --with-html-dir=${_docdir} \ --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" atk.pc

module.script-header:
case "${SB_SYSTYPE}" in
*-aix*)
  _libdir="lib/ppc64" ;;
ia64*-hpux*)
  _libdir="lib/hpux64" ;;
*-hpux*)
  _libdir="lib/pa20_64" ;;
*-irix*)
  _libdir=lib64 ;;
i?86*-solaris*)
  _libdir="lib/amd64" ;;
*-solaris*)
  _libdir="lib/sparcv9" ;;
esac

case "${SB_SYSTYPE}" in
*-aix*)
  export OBJECT_MODE=64 ;;
esac
