program.install:
${SB_PATH_INSTALL} -d -m 755 -o root -g 0 ${SB_INSTALL_PREFIX} \
${SB_INSTALL_PREFIX}/bin ${_datadir} ${_docdir}

# install as bibtex8 so as not to conflict with bibtex from tetex
${SB_PATH_INSTALL} -m 755 -o root -g 0 bibtex \
${SB_INSTALL_PREFIX}/bin/bibtex8

${SB_PATH_INSTALL} -m 644 -o root -g 0 *.csf ${_datadir}
${SB_PATH_INSTALL} -m 644 -o root -g 0 00readme.txt ${_docdir}
