program.install:
gmake install

# Modify paths in man pages
(cd ${SB_INSTALL_PREFIX}/man
 find . -type f | while read _man; do
   ${SB_PATH_SED} -i -e "\
s!/etc/named.conf!${_sysconfdir}/named.conf!; \
s!/etc/rndc.conf!${_sysconfdir}/rndc.conf!; \
s!/etc/rndc.key!${_sysconfdir}/rndc.key!; \
s!/var/run/lwresd.pid!${_localstatedir}/run/lwresd.pid!; \
s!/var/run/named.pid!${_localstatedir}/run/named.pid!" ${_man}
 done

 ${SB_PATH_SED} -i -e "\
s!/etc!${_sysconfdir}!" man8/rndc-confgen.8)

${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${SB_INSTALL_PREFIX}/doc \
${SB_INSTALL_PREFIX}/doc/draft ${SB_INSTALL_PREFIX}/doc/html \
${SB_INSTALL_PREFIX}/doc/misc ${SB_INSTALL_PREFIX}/doc/rfc
${SB_PATH_INSTALL} -m 644 -o root -g 0 doc/draft/* \
${SB_INSTALL_PREFIX}/doc/draft
${SB_PATH_INSTALL} -m 644 -o root -g 0 doc/arm/*.html \
${SB_INSTALL_PREFIX}/doc/html
${SB_PATH_INSTALL} -m 644 -o root -g 0 doc/misc/* \
${SB_INSTALL_PREFIX}/doc/misc
${SB_PATH_INSTALL} -m 644 -o root -g 0 doc/rfc/* \
${SB_INSTALL_PREFIX}/doc/rfc

${SB_PATH_INSTALL} -m 755 -o named -g 0 -d ${_sysconfdir} \
${_localstatedir}/log ${_localstatedir}/run
${SB_PATH_INSTALL} -m 644 -o named -g 0 /dev/null ${_sysconfdir}/lwresd.conf
${SB_PATH_INSTALL} -m 644 -o named -g 0 /dev/null ${_sysconfdir}/named.conf
${SB_PATH_INSTALL} -m 640 -o named -g 0 /dev/null ${_sysconfdir}/rndc.conf
${SB_PATH_INSTALL} -m 640 -o named -g 0 /dev/null ${_sysconfdir}/rndc.key
${SB_PATH_INSTALL} -m 644 -o named -g 0 /dev/null ${_sysconfdir}/db.localhost
${SB_PATH_INSTALL} -m 644 -o named -g 0 /dev/null ${_sysconfdir}/db.0
${SB_PATH_INSTALL} -m 644 -o named -g 0 /dev/null ${_sysconfdir}/db.127
${SB_PATH_INSTALL} -m 644 -o named -g 0 /dev/null ${_sysconfdir}/db.255
${SB_PATH_INSTALL} -m 644 -o named -g 0 ${SB_SRC_DB_CACHE} \
${_sysconfdir}/db.cache

cat >${_sysconfdir}/named.conf <<_EOT_
#
# Minimal default BIND configuration.
#

_EOT_

cat >${_sysconfdir}/rndc.conf <<_EOT_
options {
  default-server localhost;
  default-key "rndc-key";
};

server localhost {
  key "rndc-key";
};

include "${_sysconfdir}/rndc.key";
_EOT_

cat >${_sysconfdir}/named.conf <<_EOT_
include "${_sysconfdir}/rndc.key";

controls {
  inet 127.0.0.1 allow {
    127.0.0.1;
  };
};

logging {
  channel log {
    file "${_localstatedir}/log/named";
    print-category yes;
    print-severity no;
    print-time yes;
  };

  category lame-servers { null; };
  category default { log; };
  category security { default_syslog; log; };
};

options {
  directory "${_sysconfdir}";
  pid-file "${_localstatedir}/run/named.pid";
  notify-source * port 53;
  transfer-source * port 53;
  cleaning-interval 60;
};

zone "." IN {
  type hint;
  file "db.cache";
};

zone "localhost" IN {
  type master;
  file "db.localhost";
  notify no;
  allow-update { none; };
};

zone "127.in-addr.arpa" IN {
  type master;
  file "db.127";
  notify no;
  allow-update { none; };
};

zone "0.in-addr.arpa" IN {
  type master;
  file "db.0";
  notify no;
  allow-update { none; };
};

zone "255.in-addr.arpa" IN {
  type master;
  file "db.255";
  notify no;
  allow-update { none; };
};
_EOT_

cat >${_sysconfdir}/db.localhost <<'_EOT_'
;
; data file for local loopback interface
;

$TTL 7D
@	IN	SOA	localhost. root.localhost. (
			1	; Serial
			7D	; Refresh
			1D	; Retry
			28D	; Expire
			7D )	; Negative Cache TTL

@	IN	NS	localhost.
@	IN	A	127.0.0.1
_EOT_

cat >${_sysconfdir}/db.0 <<'_EOT_'
;
; reverse data file for broadcast zone
;

$TTL 7D
@	IN	SOA	localhost. root.localhost. (
			1	; Serial
			7D	; Refresh
			1D	; Retry
			28D	; Expire
			7D )	; Negative Cache TTL

@	IN	NS	localhost.
_EOT_

cat >${_sysconfdir}/db.127 <<'_EOT_'
;
; reverse data file for local loopback interface
;

$TTL 7D
@	IN	SOA	localhost. root.localhost. (
			1	; Serial
			7D	; Refresh
			1D	; Retry
			28D	; Expire
			7D )	; Negative Cache TTL

@	IN	NS	localhost.
1.0.0	IN	PTR	localhost.
_EOT_

cat >${_sysconfdir}/db.255 <<'_EOT_'
;
; reverse data file for broadcast zone
;

$TTL 7D
@	IN	SOA	localhost. root.localhost. (
			1	; Serial
			7D	; Refresh
			1D	; Retry
			28D	; Expire
			7D )	; Negative Cache TTL

@	IN	NS	localhost.
_EOT_
