program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" LDFLAGS="-Wl,+nodefaultrpath" ;; *-hpux11.[01]*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +DAportable \ +Oentrysched +Odataprefetch +Onolimit" ;; *-hpux*) # +O2 +Onolimit causes basic copy test to hang on hpux11.23 CC=cc CFLAGS="-z +O1 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch" ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 -diag_error 1035 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0"
export ac_cv_prog_cc_c99=no ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ ${LDFLAGS+LDFLAGS="${LDFLAGS}"} \ ${SB_VAR_LIBICONV+--with-libiconv-prefix=${SB_VAR_LIBICONV}} \ ${SB_VAR_GETTEXT+--with-libintl-prefix=${SB_VAR_GETTEXT}} \ --datarootdir=${_datadir} --datadir=${_datadir} \ --disable-silent-rules --disable-dependency-tracking \ --docdir=${_docdir} --mandir="${SB_INSTALL_PREFIX}/man" \ --infodir="${SB_INSTALL_PREFIX}/info" \ --prefix=${SB_INSTALL_PREFIX}
