program.install:
${SB_VAR_PYTHON}/bin/python setup.py install --skip-build \
--install-lib=${_install_prefix} --install-data=${_datadir} \
${_bindir+--install-scripts=${_bindir}} \
--prefix=${SB_INSTALL_PREFIX}

if [ "${_bindir+set}" != "set" ]; then
  ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_docdir}
  ${SB_PATH_INSTALL} -m 644 -o root -g 0 *.txt ${_docdir}
  tar cf - docs | (cd ${_docdir}; tar xf -)
  ${SB_PATH_CHOWN} -hR 0:0 ${_docdir}
  find ${_docdir} -type f -exec ${SB_PATH_CHMOD} 644 {} \;
  find ${_docdir} -type d -exec ${SB_PATH_CHMOD} 755 {} \;

  ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_sysconfdir}
  ${SB_PATH_INSTALL} -m 644 -o root -g 0 /dev/null \
  ${_sysconfdir}/docutils.conf

  for _bin in ${SB_INSTALL_PREFIX}/bin/*; do
    ${SB_PATH_SED} -i -e "\
s!@libdir@!${_install_prefix}!;" ${_bin}
    mv ${_bin} ${_bin%%.py}
  done
fi

# install path for Python's include.paths.d
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \
${_install_prefix}/include.paths.d
cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_
${_install_prefix}
_EOT_

