program.script-header:
_gs_datadir="${SB_VAR_GHOSTSCRIPT}/share"
_docdir="${SB_INSTALL_PREFIX}/doc"

case "${SB_SYSTYPE}" in
*-aix*)
  PLATFORM=aix-xlc ;;
ia64*-hpux*)
  PLATFORM=hpuxi-acc-32 ;;
*-hpux*)
  PLATFORM=hpux-g++ ;;
*-irix*)
  PLATFORM=irix-cc ;;
i?86*-linuxe4)
  PLATFORM=linux-g++ ;;
i?86*-linux*)
  PLATFORM=rhel5-g++ ;;
x86_64-redhat-linuxe4)
  PLATFORM="linux-g++-64" ;;
x86_64-redhat-linux*)
  PLATFORM="rhel5-g++-64" ;;
*-osf*)
  PLATFORM=tru64-g++ ;;
i?86*-solaris*)
  PLATFORM=solarisx86-cc59 ;;
*-solaris2.8)
  PLATFORM=solaris-g++ ;;
*-solaris*)
  PLATFORM=solaris-cc59 ;;
esac

export QMAKESPEC=${PLATFORM}

