program.install:
gmake install
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_datadir}/pixmaps (cd ${_datadir}/pixmaps bzip2 -dc ${SB_SRC_ICONS} | gtar xf - mv fvwm_icons/* ${_datadir}/pixmaps) rm -rf ${_datadir}/pixmaps/fvwm_icons ${SB_PATH_CHOWN} -hR 0:0 ${_datadir}/pixmaps ${SB_PATH_FIND} ${_datadir}/pixmaps -type f -exec chmod 644 {} \;
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_sysconfdir} ${SB_PATH_INSTALL} -m 644 -o root -g 0 sample.fvwmrc/system.fvwm2rc \ ${_sysconfdir}
