program.configure:
case "${SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Onofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux10*|*-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Onofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Onofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O1 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_GETTEXT+-I${SB_VAR_GETTEXT_INC}} \ ${SB_VAR_INCONV+-I${SB_VAR_ICONV_INC}} ${CPPFLAGS+${CPPFLAGS}}" \ LDFLAGS="${SB_VAR_FCPACKAGE+-L${SB_VAR_FCPACKAGE_LIB}} \ ${SB_VAR_LIBPANGO+-L${SB_VAR_LIBPANGO_LIB}} \ ${SB_VAR_LIBGTK+-L${SB_VAR_LIBGTK_LIB}} \ ${SB_VAR_LIBGLADE+-L${SB_VAR_LIBGLADE_LIB}} \ ${SB_VAR_GETTEXT+-L${SB_VAR_GETTEXT_LIB}} \ ${SB_VAR_INCONV+-L${SB_VAR_ICONV_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="${SB_VAR_FCPACKAGE+${SB_VAR_FCPACKAGE_LIB}/pkgconfig:}\ ${SB_VAR_LIBGLIB+${SB_VAR_LIBGLIB_LIB}/pkgconfig:}\ ${SB_VAR_LIBGDKPIXBUF+${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig:}\ ${SB_VAR_LIBGTK+${SB_VAR_LIBGTK_LIB}/pkgconfig:}\ ${SB_VAR_LIBPANGO+${SB_VAR_LIBPANGO_LIB}/pkgconfig:}\ ${SB_VAR_LIBPNG+${SB_VAR_LIBPNG_LIB}/pkgconfig:}\ ${SB_VAR_LIBCAIRO+${SB_VAR_LIBCAIRO_LIB}/pkgconfig:}\ ${SB_VAR_LIBATK+${SB_VAR_LIBATK_LIB}/pkgconfig:}\ ${SB_VAR_LIBXML+${SB_VAR_LIBXML_LIB}/pkgconfig:}\ ${SB_VAR_LIBTTF+${SB_VAR_LIBTTF_LIB}/pkgconfig:}\ ${SB_VAR_LIBGLADE+${SB_VAR_LIBGLADE_LIB}/pkgconfig:}\ ${SB_VAR_GETTEXT+${SB_VAR_GETTEXT_LIB}/pkgconfig:}\ ${SB_VAR_LIBZ+${SB_VAR_LIBZ_LIB}/pkgconfig:}\ ${SB_VAR_LIBICONV+${SB_VAR_LIBICONV_LIB}/pkgconfig}" \ --datadir=${_datadir} --datarootdir=${_datadir} \ --mandir="${SB_INSTALL_PREFIX}/man" --disable-dependency-tracking \ --prefix=${SB_INSTALL_PREFIX}
# Regenerate flex files rm -f src/flex_parser.c
