module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" CXX=xlC CXXFLAGS="-O2 -qro -qroconst -qnotempinc \ -qnotemplateregistry -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CXX=aCC CXXFLAGS="-AA -z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux*) CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CXX=CC CXXFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -ptused -FE:template_in_elf_section -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CXXFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CXX=cxx CXXFLAGS="-O2 -arch ev5 -model ansi -msym \ -version V7.1-006 -readonly_strings -timplicit_local \ -D__USE_STD_IOSTREAM" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CFLAGS="-O2 -mtune=pentium4 -march=pentium4" CXXFLAGS="-O2 -mtune=pentium4 -march=pentium4" CC_LD_RT="-R" ;; *-solaris*) CFLAGS="-O2 -mcpu=ultrasparc -mvis" CXXFLAGS="-O2 -mcpu=ultrasparc -mvis" CC_LD_RT="-R" ;; esac
set -x ${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CXX="${CXX:-g++}" CXXFLAGS="${CXXFLAGS:--O2}" \ LDFLAGS="${SB_VAR_GCC_RT+-L${SB_VAR_GCC_RT_LIB} \ ${CC_LD_RT}${SB_VAR_GCC_RT_LIB}} ${LDFLAGS+${LDFLAGS}}" \ MAKE=gmake PKG_CONFIG_PATH="\ ${_libcairomm_lib:-${SB_VAR_LIBCAIROMM_LIB}\ ${SB_VAR_GCC_RT+/${_gccsubdir}}/pkgconfig}:\ ${_libglibmm_lib:-${SB_VAR_LIBGLIBMM_LIB}\ ${SB_VAR_GCC_RT+/${_gccsubdir}}/pkgconfig}:\ ${_libgtkmm_lib:-${SB_VAR_LIBGTKMM_LIB}\ ${SB_VAR_GCC_RT+/${_gccsubdir}}/pkgconfig}:\ ${_libsigc_lib:-${SB_VAR_LIBSIGC_LIB}\ ${SB_VAR_GCC_RT+/${_gccsubdir}}/pkgconfig}\ ${SB_VAR_FCPACKAGE+:${SB_VAR_FCPACKAGE_LIB}/pkgconfig}\ ${SB_VAR_GETTEXT+:${SB_VAR_GETTEXT_LIB}/pkgconfig}\ ${SB_VAR_GNOME+:${SB_VAR_GNOME_LIB}/pkgconfig}\ ${SB_VAR_LIBATK+:${SB_VAR_LIBATK_LIB}/pkgconfig}\ ${SB_VAR_LIBCAIRO+:${SB_VAR_LIBCAIRO_LIB}/pkgconfig}\ ${SB_VAR_LIBGLIB+:${_libglib_lib:-${SB_VAR_LIBGLIB_LIB}\ ${SB_VAR_GCC_RT+/${_gccsubdir}}/pkgconfig}}\ ${SB_VAR_LIBGTK+:${SB_VAR_LIBGTK_LIB}/pkgconfig}\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}\ ${SB_VAR_LIBPANGO+:${SB_VAR_LIBPANGO_LIB}/pkgconfig}\ ${SB_VAR_LIBTTF+:${SB_VAR_LIBTTF_LIB}/pkgconfig}" \ --disable-dependency-tracking --enable-warnings=no \ ${ARGS+${ARGS}} --disable-documentation \ --libdir=${_libdir:-${SB_INSTALL_PREFIX_LIB}${SB_VAR_GCC_RT+/${_gccsubdir}}} \ --datarootdir=${_datadir} --datadir=${_datadir} \ --docdir=${_docdir} --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" gconf/gconfmm-2.6.pc

case "${SB_SYSTYPE}" in *-aix*) CFLAGS="-O2 -mcpu=powerpc" CXXFLAGS="-O2 -mcpu=powerpc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC_LD_RT="-Wl,+b," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CXXFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-irix*|*-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CFLAGS="-O2 -mcpu=ev5" CXXFLAGS="-O2 -mcpu=ev5" CC_LD_RT="-Wl,-rpath," ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CXX="${CXX:-g++}" CXXFLAGS="${CXXFLAGS:--O2}" \ LDFLAGS="-L${SB_VAR_GCC_RT_LIB} ${CC_LD_RT}${SB_VAR_GCC_RT_LIB} \ ${LDFLAGS+${LDFLAGS}}" MAKE=gmake \ PKG_CONFIG_PATH="${SB_VAR_FCPACKAGE_LIB}/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig:\ ${SB_VAR_GNOME_LIB}/pkgconfig:\ ${SB_VAR_LIBATK_LIB}/pkgconfig:\ ${SB_VAR_LIBCAIRO_LIB}/pkgconfig:\ ${SB_VAR_LIBCAIROMM_LIB}/${_gccsubdir}/pkgconfig:\ ${SB_VAR_LIBGLIB_LIB}/${_gccsubdir}/pkgconfig:\ ${SB_VAR_LIBGLIBMM_LIB}/${_gccsubdir}/pkgconfig:\ ${SB_VAR_LIBGTK_LIB}/pkgconfig:\ ${SB_VAR_LIBGTKMM_LIB}/${_gccsubdir}/pkgconfig:\ ${SB_VAR_LIBICONV_LIB}/pkgconfig:\ ${SB_VAR_LIBPANGO_LIB}/pkgconfig:\ ${SB_VAR_LIBSIGC_LIB}/${_gccsubdir}/pkgconfig:\ ${SB_VAR_LIBTTF_LIB}/pkgconfig" \ --disable-dependency-tracking --enable-warnings=no \ --datarootdir=${_datadir} --datadir=${_datadir} \ ${ARGS+${ARGS}} --disable-documentation \ --libdir="${SB_INSTALL_PREFIX_LIB}/${_gccsubdir}" \ --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" gconf/gconfmm-2.6.pc
