module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="+O2 -Ae -z -Wp,-H32768 +Ofltacc +ESlit \ +DAportable +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CPPFLAGS="-DANSICPP" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CPPFLAGS="-DANSICPP" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CPPFLAGS="-DANSICPP" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" \ CPPFLAGS="${SB_VAR_LIBPNG+-I${SB_VAR_LIBPNG_INC}} \ ${SB_VAR_LIBZ+-I${SB_VAR_LIBZ_INC}} -I${SB_VAR_JASPER_INC} \ ${SB_VAR_JPEG+-I${SB_VAR_JPEG_INC}} \ ${SB_VAR_LIBTIFF+-I${SB_VAR_LIBTIFF_INC}} \ ${CPPFLAGS+${CPPFLAGS}}" CFLAGS="${CFLAGS:--O2}" \ LDFLAGS="${SB_VAR_LIBPNG+-L${SB_VAR_LIBPNG_LIB}} \ ${SB_VAR_LIBZ+-L${SB_VAR_LIBZ_LIB}} \ -L${SB_VAR_JASPER_LIB} ${SB_VAR_JPEG+-L${SB_VAR_JPEG_LIB}} \ ${SB_VAR_LIBTIFF+-L${SB_VAR_LIBTIFF_LIB}} \ ${CC_LD_RT}${SB_VAR_JASPER_LIB}\ ${SB_VAR_LIBPNG+:${SB_VAR_LIBPNG_LIB}}\ ${SB_VAR_LIBZ+:${SB_VAR_LIBZ_LIB}}\ ${SB_VAR_JPEG+:${SB_VAR_JPEG_LIB}}\ ${SB_VAR_LIBTIFF+:${SB_VAR_LIBTIFF_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="${SB_VAR_LIBGLIB_LIB}/pkgconfig:\ ${SB_VAR_JASPER_LIB}/pkgconfig\ ${SB_VAR_GETTEXT+:${SB_VAR_GETTEXT_LIB}/pkgconfig}\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}\ ${SB_VAR_LIBPNG+:${SB_VAR_LIBPNG_LIB}/pkgconfig}\ ${SB_VAR_LIBZ+:${SB_VAR_LIBZ_LIB}/pkgconfig}" \ ${SB_VAR_LIBICONV+--with-libiconv-prefix=${SB_VAR_LIBICONV}} \ ${SB_VAR_GETTEXT+--with-libintl-prefix=${SB_VAR_GETTEXT}} \ --enable-debug=no --with-libjasper --datarootdir=${_datadir} \ --datadir=${_datadir} --sysconfdir=${_sysconfdir} \ --with-html-dir=${_docdir} --disable-gtk-doc --disable-man \ --enable-static --enable-shared --disable-dependency-tracking \ --enable-explicit-deps ${ARGS+${ARGS}} \ --mandir="${SB_INSTALL_PREFIX}/man" --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s! -L/usr/lib64!!; \ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" \ gdk-pixbuf-2.0.pc contrib/gdk-pixbuf-xlib/gdk-pixbuf-xlib-2.0.pc
