module.install:
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_install_prefix}
PYTHONPATH="${SB_VAR_SETUPTOOLS_LIB}/${_pysubdir}:${_install_prefix}" \ ${SB_VAR_PYTHON}/bin/python setup.py install \ --single-version-externally-managed --root=/ \ --install-lib=${_install_prefix} --prefix=${SB_INSTALL_PREFIX}
# install path for Python's include.paths.d ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \ ${_install_prefix}/include.paths.d cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_ ${_install_prefix} _EOT_

${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_install_prefix}
PYTHONPATH="${SB_VAR_SETUPTOOLS_LIB}/${_pysubdir}:${_install_prefix}" \ ${SB_VAR_PYTHON}/bin/python setup.py install \ --single-version-externally-managed --root=/ \ --install-lib=${_install_prefix} --prefix=${SB_INSTALL_PREFIX}
# install path for Python's include.paths.d ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \ ${_install_prefix}/include.paths.d cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_ ${_install_prefix} _EOT_
