module.install:
gmake install DOCDIR=${_docdir}
rmdir ${SB_INSTALL_PREFIX}/share/gimp-print/samples rmdir ${SB_INSTALL_PREFIX}/share/gimp-print rmdir ${SB_INSTALL_PREFIX}/lib/gimp/1.2/plug-ins rmdir ${SB_INSTALL_PREFIX}/lib/gimp/1.2 rmdir ${SB_INSTALL_PREFIX}/lib/gimp
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_docdir}/texinfo ${SB_PATH_INSTALL} -m 644 -o root -g 0 doc/*.texi doc/print-color.* \ doc/print-main.* doc/print-setup.* ${_docdir}/texinfo
