program.install:
gmake install

# install texinfo source files
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_docdir}
${SB_PATH_INSTALL} -m 644 -o root -g 0 docs/glib.texi ${_docdir}


case "${SB_SYSTYPE}" in
*-aix*)
  CC_LD_RT="-Wl,-blibpath:" LDFLAGS=":/usr/lib" ;;
*-osf*|*-irix6*|*-linux*)
  CC_LD_RT="-Wl,-rpath," ;;
*-hpux*)
  CC_LD_RT="-Wl,+s,+b," ;;
*-solaris2*)   
  CC_LD_RT="-R" ;;
esac

${SB_PATH_SED} -i -e "\
s!-bexpall!-Wl,-bexpall!; \
s!\${exec_prefix}/lib/!${SB_INSTALL_PREFIX_LIB}!; \
s!-L\(${SB_INSTALL_PREFIX_LIB}\) !-L\1 ${CC_LD_RT}\1 !;" \
${SB_INSTALL_PREFIX}/bin/glib-config
