module.configure:
case "${SB_SYSTYPE}" in *-aix*) LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib -Wl,-bI:${_python_exp}" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC_LD_RT="-Wl,+b," ;; *-hpux*) CC_LD_RT="-Wl,+s,+b," ;; *-irix*) # add /usr/lib32 to work around ld bug # LDFLAGS="-L/usr/lib32 -L${SB_VAR_GETTEXT_LIB} -L${SB_VAR_LIBICONV_LIB}" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CFLAGS="-O2 -mcpu=ev5" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CFLAGS="-O2 -mtune=pentium4 -march=pentium4" CC_LD_RT="-Wl,-R" ;; *-solaris*) CFLAGS="-O2 -mcpu=ultrasparc -mvis" CC_LD_RT="-Wl,-R" ;; esac
PYTHONPATH="${SB_VAR_LIBGLIB_LIB}/${_pysubdir}:\ ${SB_VAR_LIBCAIRO_LIB}/${_pysubdir}:\ ${SB_VAR_NUMPY_LIB}/${_pysubdir}" \ ${SHELL} ./configure CC=gcc CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="-I${SB_VAR_PYTHONP}/include/python2.7 \ ${CPPFLAGS+${CPPFLAGS}}" LDFLAGS="${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBGLIB_LIB}/${_gcc_subdir+${_gcc_subdir}/}pkgconfig:\ ${SB_VAR_FCPACKAGE_LIB}/pkgconfig:${SB_VAR_LIBGTK_LIB}/pkgconfig:\ ${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig:${SB_VAR_LIBPNG_LIB}/pkgconfig:\ ${SB_VAR_LIBZ_LIB+${SB_VAR_LIBZ_LIB}/pkgconfig:}\ ${SB_VAR_LIBCAIRO_LIB}/pkgconfig:${SB_VAR_LIBPANGO_LIB}/pkgconfig:\ ${SB_VAR_LIBATK_LIB}/pkgconfig:${SB_VAR_LIBGLADE_LIB}/pkgconfig:\ ${SB_VAR_LIBXML_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig:${SB_VAR_LIBFFI_LIB}/pkgconfig:\ ${SB_VAR_LIBCAIRO_LIB}/${_pysubdir}/lib/pkgconfig:\ ${SB_VAR_LIBGLIB_LIB}/${_pysubdir}/lib/pkgconfig\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}" \ am_cv_python_pyexecdir=${_install_prefix} \ --disable-dependency-tracking \ --bindir="${_install_prefix}/bin" \ --prefix=${_install_prefix}
${SB_PATH_SED} -i -e "\ s|pygobject_script=.*|\ pygobject_script=${SB_VAR_LIBGLIB_LIB}/${_pysubdir}/bin/pygobject-codegen-2.0|" \ pygtk-codegen-2.0

module.install:
gmake install
# Install required pygtk.py too! ${SB_PATH_INSTALL} -m 644 -o root -g 0 \ pygtk.py ${_install_prefix}
# install path for Python's include.paths.d ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \ ${_install_prefix}/include.paths.d cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_ ${_install_prefix} ${_install_prefix}/gtk-2.0 _EOT_

module.configure:
case "${SB_SYSTYPE}" in *-aix*) LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib -Wl,-bI:${_python_exp}" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC_LD_RT="-Wl,+b," ;; *-hpux*) CC_LD_RT="-Wl,+s,+b," ;; *-irix*) # add /usr/lib32 to work around ld bug # LDFLAGS="-L/usr/lib32 -L${SB_VAR_GETTEXT_LIB} -L${SB_VAR_LIBICONV_LIB}" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CFLAGS="-O2 -mcpu=ev5" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CFLAGS="-O2 -mtune=pentium4 -march=pentium4" CC_LD_RT="-Wl,-R" ;; *-solaris*) CFLAGS="-O2 -mcpu=ultrasparc -mvis" CC_LD_RT="-Wl,-R" ;; esac
PYTHONPATH="${SB_VAR_LIBGLIB_LIB}/${_pysubdir}:\ ${SB_VAR_LIBCAIRO_LIB}/${_pysubdir}:\ ${SB_VAR_NUMPY_LIB}/${_pysubdir}" \ ${SHELL} ./configure CC=gcc CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="-I${SB_VAR_PYTHONP}/include/python2.6 \ ${CPPFLAGS+${CPPFLAGS}}" LDFLAGS="${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBGLIB_LIB}/${_gcc_subdir+${_gcc_subdir}/}pkgconfig:\ ${SB_VAR_FCPACKAGE_LIB}/pkgconfig:${SB_VAR_LIBGTK_LIB}/pkgconfig:\ ${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig:\ ${SB_VAR_LIBPNG_LIB}/pkgconfig:\ ${SB_VAR_LIBZ_LIB+${SB_VAR_LIBZ_LIB}/pkgconfig:}\ ${SB_VAR_LIBCAIRO_LIB}/pkgconfig:${SB_VAR_LIBPANGO_LIB}/pkgconfig:\ ${SB_VAR_LIBATK_LIB}/pkgconfig:${SB_VAR_LIBGLADE_LIB}/pkgconfig:\ ${SB_VAR_LIBXML_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig:${SB_VAR_LIBFFI_LIB}/pkgconfig:\ ${SB_VAR_LIBCAIRO_LIB}/${_pysubdir}/lib/pkgconfig:\ ${SB_VAR_LIBGLIB_LIB}/${_pysubdir}/lib/pkgconfig\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}" \ am_cv_python_pyexecdir=${_install_prefix} \ --disable-dependency-tracking \ --bindir="${_install_prefix}/bin" \ --prefix=${_install_prefix}
${SB_PATH_SED} -i -e "\ s|pygobject_script=.*|\ pygobject_script=${SB_VAR_LIBGLIB_LIB}/${_pysubdir}/bin/pygobject-codegen-2.0|" \ pygtk-codegen-2.0

