gmake install
# Install required pygtk.py too! ${SB_PATH_INSTALL} -m 644 -o root -g 0 \ pygtk.py ${_install_prefix}
# install path for Python's include.paths.d ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \ ${_install_prefix}/include.paths.d cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_ ${_install_prefix} ${_install_prefix}/gtk-2.0 _EOT_

module.configure:
PERL5LIB="${SB_VAR_LIBGLIB_LIB}/perl5122:${SB_VAR_LIBCAIRO_LIB}/perl5122:\ ${SB_VAR_LIBPANGO_LIB}/perl5122" \ PKG_CONFIG_PATH="${SB_VAR_LIBGLIB_LIB}/${_gcc_subdir}/pkgconfig:\ ${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig:\ ${SB_VAR_LIBPNG_LIB}/pkgconfig:\ ${SB_VAR_LIBZ_LIB}/pkgconfig:\ ${SB_VAR_LIBGTK_LIB}/pkgconfig:${SB_VAR_LIBPANGO_LIB}/pkgconfig:\ ${SB_VAR_LIBCAIRO_LIB}/pkgconfig:${SB_VAR_FCPACKAGE_LIB}/pkgconfig:\ ${SB_VAR_LIBATK_LIB}/pkgconfig:${SB_VAR_LIBTTF_LIB}/pkgconfig:\ ${SB_VAR_GETTEXT_LIB}/pkgconfig" \ ${SB_VAR_PERL}/bin/perl Makefile.PL \ INSTALLARCHLIB=${_install_prefix} \ INSTALLSITEARCH=${_install_prefix} \ INSTALLSITELIB=${_install_prefix} \ PREFIX=${SB_INSTALL_PREFIX}
# fix TOC overflow on AIX ${SB_PATH_SED} -i -e "\ s!\(-Wl,-bE[^ ][^ ]*\) !\1 -Wl,-bbigtoc !;" Makefile
# remove system paths ${SB_PATH_SED} -i -e "\ s!${SB_VAR_LIBGTK}/lib/hpux32!${SB_VAR_LIBGTK_LIB}!; \ s!:/usr/X11R6/lib64!!; \ s!:/usr/X11R6/lib!!; \ s!:/usr/lib/hpux32!!; \ s!:/usr/openwin/lib!!; \ s!:/usr/shlib!!; \ s!:/usr/lib64!!; \ s!:/usr/lib!!; \ s!:/lib64!!; \ s!:/lib!!;" Makefile
