module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" CC_LD_RT="-Wl,-blibpath:" LDFLAGS="-Wl,-brtl" ;; ia64*-hpux*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ LDFLAGS="${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBATK+${SB_VAR_LIBATK_LIB}/pkgconfig:}\ ${SB_VAR_LIBCAIRO+${SB_VAR_LIBCAIRO_LIB}/pkgconfig:}\ ${SB_VAR_FCPACKAGE+${SB_VAR_FCPACKAGE_LIB}/pkgconfig:}\ ${SB_VAR_LIBTTF+${SB_VAR_LIBTTF_LIB}/pkgconfig:}\ ${SB_VAR_GETTEXT+${SB_VAR_GETTEXT_LIB}/pkgconfig:}\ ${SB_VAR_LIBGLIB+${SB_VAR_LIBGLIB_LIB}/pkgconfig:}\ ${SB_VAR_LIBGDKPIXBUF+${SB_VAR_LIBGDKPIXBUF_LIB}/pkgconfig:}\ ${SB_VAR_LIBGTK+${SB_VAR_LIBGTK_LIB}/pkgconfig:}\ ${SB_VAR_LIBICONV+${SB_VAR_LIBICONV_LIB}/pkgconfig:}\ ${SB_VAR_LIBPNG+${SB_VAR_LIBPNG_LIB}/pkgconfig:}\ ${SB_VAR_LIBPANGO+${SB_VAR_LIBPANGO_LIB}/pkgconfig:}\ ${SB_VAR_LIBZ+${SB_VAR_LIBZ_LIB}/pkgconfig}" \ --enable-shared --enable-static \ --mandir="${SB_INSTALL_PREFIX}/man" \ --datarootdir=${_datadir} --datadir=${_datadir} \ --docdir=${_docdir} --disable-gtk-doc \ --with-html-dir="${_docdir}/html" \ --disable-dependency-tracking \ --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" gtkimageview.pc
