module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" CC_LD_RT="-Wl,-blibpath:" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Onofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-z +O2 +Onofltacc +Olit=all +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Onofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_NCURSES+-I${SB_VAR_NCURSES_INC}}" \ LDFLAGS="${SB_VAR_NCURSES+-L${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_NCURSES+${CC_LD_RT}${SB_VAR_NCURSES_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBGLIB+${SB_VAR_LIBGLIB_LIB}/pkgconfig}\ ${SB_VAR_GETTEXT+:${SB_VAR_GETTEXT_LIB}/pkgconfig}" \ --disable-dependency-tracking --datadir=${_datadir} \ --datarootdir=${_datadir} --sysconfdir=${_sysconfdir} \ --enable-ipv6 --disable-ssl --with-terminfo --with-perl=no \ --docdir=${_docdir} --libdir=${SB_INSTALL_PREFIX_LIB} \ --mandir="${SB_INSTALL_PREFIX}/man" \ --prefix=${SB_INSTALL_PREFIX}
# fix pathnames ${SB_PATH_SED} -i -e "\ s!/etc!${_sysconfdir}!; \ s!/usr/share/irssi!${_datadir}!;" docs/irssi.1

case "${SB_SYSTYPE}" in *-aix*) _perl_lib_path=$(perl -MExtUtils::Embed -e ldopts | \ xargs -n1 | egrep 'CORE$' | sed -e 's/^-L//') LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib:${_perl_lib_path}" CC_LD_RT="-Wl,-blibpath:" ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC_LD_RT="-R" ;; *-solaris*) CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_NCURSES+-I${SB_VAR_NCURSES_INC}}" \ LDFLAGS="${SB_VAR_GCC_RT+-L${SB_VAR_GCC_RT_LIB}} \ ${SB_VAR_NCURSES+-L${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_GCC_RT+${CC_LD_RT}${SB_VAR_GCC_RT_LIB}} \ ${SB_VAR_NCURSES+${CC_LD_RT}${SB_VAR_NCURSES_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBGLIB+${SB_VAR_LIBGLIB_LIB}/pkgconfig}\ ${SB_VAR_GETTEXT+:${SB_VAR_GETTEXT_LIB}/pkgconfig}" \ --disable-dependency-tracking --datadir=${_datadir} \ --datarootdir=${_datadir} --sysconfdir=${_sysconfdir} \ --enable-ipv6 --disable-ssl --with-terminfo --with-perl=module \ --docdir=${_docdir} --libdir=${SB_INSTALL_PREFIX_LIB} \ --with-perl-lib="${SB_INSTALL_PREFIX_LIB}/perl5122" \ --mandir="${SB_INSTALL_PREFIX}/man" \ --prefix=${SB_INSTALL_PREFIX}
case "${SB_SYSTYPE}" in *-linuxe4*) ${SB_PATH_SED} -i -e 's|-fstack-protector||' src/perl/Makefile ;; esac
