program.script-header:
_docdir="${SB_INSTALL_PREFIX}/doc"

case "${SB_SYSTYPE}" in
*-aix*)
  export JAVA_HOME=/usr/java5 ;;
*-hpux*)
  export JAVA_HOME=/opt/java1.5 ;;
*-linux*)
  export JAVA_HOME=/opt/java1.5 ;;
*-solaris*)
  export JAVA_HOME=/opt/java1.5 ;;
esac

program.install:
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${SB_INSTALL_PREFIX}/lib \
${_docdir}

${SB_PATH_INSTALL} -m 644 -o root -g 0 \
${SB_PROG_NAME}${SB_PROG_VER}/junit-${SB_PROG_VER}.jar \
${SB_INSTALL_PREFIX}/lib/junit.jar

${SB_PATH_INSTALL} -m 644 -o root -g 0 \
${SB_PROG_NAME}${SB_PROG_VER}/README.html ${_docdir}
(cd doc
 ${SB_PATH_FIND} . -print | cpio -pdmu ${_docdir})
${SB_PATH_FIND} ${_docdir} -type d -exec ${SB_PATH_CHMOD} 755 {} \;
${SB_PATH_FIND} ${_docdir} -type f -exec ${SB_PATH_CHMOD} 644 {} \;
${SB_PATH_CHOWN} -hR 0:0 ${_docdir}
