module.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," X_INCLUDES="/usr/include/X11R6 -I/usr/contrib/X11R6/include" X_LIBRARIES="/usr/lib/X11R6 -L/usr/contrib/X11R6/lib" ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CPPFLAGS="-Ae" CC_LD_RT="-Wl,+s,+b," X_INCLUDES="/usr/include/X11R6 -I/usr/contrib/X11R6/include" X_LIBRARIES="/usr/lib/X11R6 -L/usr/contrib/X11R6/lib" ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," X_INCLUDES="/usr/include/X11R6 -I/usr/contrib/X11R6/include" X_LIBRARIES="/usr/lib/X11R6 -L/usr/contrib/X11R6/lib" ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -ieee -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_NCURSES+-I${SB_VAR_NCURSES_INC} \ -I${SB_VAR_NCURSES_INC}/ncurses}" \ LDFLAGS="${SB_VAR_NCURSES+-L${SB_VAR_NCURSES_LIB} \ ${CC_LD_RT}${SB_VAR_NCURSES_LIB}} ${LDFLAGS+${LDFLAGS}}" \ --enable-static --enable-shared --disable-dependency-tracking \ --mandir="${SB_INSTALL_PREFIX}/man" ${ARGS+${ARGS}} \ --libdir=${SB_INSTALL_PREFIX_LIB} --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" libedit.pc

module.script-header:
case "${SB_SYSTYPE}" in
*-aix*)
  _libdir=lib/ppc64 ;;
ia64*-hpux*)
  _libdir=lib/hpux64 ;;
*-hpux*)
  _libdir=lib/pa20_64 ;;
*-irix*)
  _libdir=lib64 ;;
i?86*-solaris*)
  _libdir=lib/amd64 ;;
*-solaris*)
  _libdir=lib/sparcv9 ;;
esac

case "${SB_SYSTYPE}" in
*-aix*)
  export OBJECT_MODE=64 ;;
esac
