module.script-header:
case "${SB_SYSTYPE}" in
*-aix*)
  _libdir="lib/ppc64" ;;
ia64*-hpux*)
  _libdir="lib/hpux64" ;;
*-hpux11.00)
  _libdir="lib/pa20_64"
  _gcclibdir="lib/${_gccsubdir}/pa20_64" ;;
*-hpux*)
  _libdir="lib/pa20_64" ;;
*-irix*)
  _libdir="lib64" ;;
i?86*-solaris*)
  _libdir="lib/amd64" ;;
*-solaris*)
  _libdir="lib/sparcv9" ;;
esac

case "${SB_SYSTYPE}" in
*-aix*)
  export OBJECT_MODE=64 ;;
esac

module.configure:
case "${SB_SYSTYPE}" in *-aix*) CFLAGS="-O2 -mcpu=powerpc" CXXFLAGS="-O2 -mcpu=powerpc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC_LD_RT="-Wl,+b," ;; *-hpux*) CC_LD_RT="-Wl,+s,+b," ;; *-irix*|*-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CFLAGS="-O2 -mcpu=ev5" CXXFLAGS="-O2 -mcpu=ev5" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CFLAGS="-O2 -mtune=pentium4 -march=pentium4" CXXFLAGS="-O2 -mtune=pentium4 -march=pentium4" CC_LD_RT="-R" ;; *-solaris*) CFLAGS="-O2 -mcpu=ultrasparc -mvis" CXXFLAGS="-O2 -mcpu=ultrasparc -mvis" CC_LD_RT="-R" ;; esac
MAKE=gmake \ ${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CXX="${CXX:-g++}" CXXFLAGS="${CXXFLAGS:--O2}" \ LDFLAGS="-L${SB_VAR_GCC_RT_LIB} ${CC_LD_RT}${SB_VAR_GCC_RT_LIB} \ ${LDFLAGS+${LDFLAGS}}" --disable-dependency-tracking \ --docdir=${_docdir} --datarootdir=${_datadir} \ --datadir=${_datadir} ${ARGS+${ARGS}} \ --libdir="${SB_INSTALL_PREFIX_LIB}/${_gccsubdir}" \ --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" sigc++-2.0.pc
