program.build:
case "${SB_SYSTYPE}" in
*-aix*)
  # all tests fail without this
  LIBS=-lpthread ;;
esac

gmake docdir=${_docdir} ${LIBS+LIBS="${LIBS}"}

module.build:
case "${SB_SYSTYPE}" in
*-aix*)
  # all tests fail without this
  LIBS=-lpthread ;;
esac

gmake docdir=${_docdir} ${LIBS+LIBS="${LIBS}"}

case "${SB_SYSTYPE}" in
*-aix*)
  # all tests fail without this
  LIBS=-lpthread ;;
esac

gmake docdir=${_docdir} ${LIBS+LIBS="${LIBS}"}
