program.configure:
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CXX="${CXX:-g++}" CXXFLAGS="${CXXFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_LIBDB+-I${SB_VAR_LIBDB_INC}} \ ${SB_VAR_LIBEXPAT+-I${SB_VAR_LIBEXPAT_INC}} \ ${SB_VAR_LIBICONV+-I${SB_VAR_LIBICONV_INC}} \ ${SB_VAR_LIBOPENSSL+-I${SB_VAR_LIBOPENSSL_INC}} \ ${SB_VAR_OPENLDAP+-I${SB_VAR_OPENLDAP_INC}} \ ${CPPFLAGS+${CPPFLAGS}}" \ LDFLAGS="${SB_VAR_LIBDB+-L${SB_VAR_LIBDB_LIB} \ ${CC_LD_RT}${SB_VAR_LIBDB_LIB}} \ ${SB_VAR_LIBEXPAT+-L${SB_VAR_LIBEXPAT_LIB} \ ${CC_LD_RT}${SB_VAR_LIBEXPAT_LIB}} \ ${SB_VAR_LIBICONV+-L${SB_VAR_LIBICONV_LIB} \ ${CC_LD_RT}${SB_VAR_LIBICONV_LIB}} \ ${SB_VAR_LIBOPENSSL+-L${SB_VAR_LIBOPENSSL_LIB} \ ${CC_LD_RT}${SB_VAR_LIBOPENSSL_LIB}} \ ${SB_VAR_OPENLDAP+-L${SB_VAR_OPENLDAP_LIB} \ ${CC_LD_RT}${SB_VAR_OPENLDAP_LIB}} \ ${SB_VAR_GCC_RT+-L${SB_VAR_GCC_RT_LIB} ${CC_LD_RT}${SB_VAR_GCC_RT_LIB}} \ ${SB_VAR_SUNSTUDIORUNTIME+${CC_LD_RT}${SB_VAR_SUNSTUDIORUNTIME_LIB}/stlport4} \ ${LDFLAGS+${LDFLAGS}}" \ --disable-dependency-tracking \ --with-apr=${_apr_config:-${SB_VAR_LIBAPR}} \ --with-apr-util=${_aprutil_config:-${SB_VAR_LIBAPRUTIL}} \ --datadir=${_datadir} ${ARGS+${ARGS}} \ --libdir=${_libdir:-${SB_INSTALL_PREFIX_LIB}} \ --mandir="${SB_INSTALL_PREFIX}/man" \ --prefix=${SB_INSTALL_PREFIX}
gmake configure
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" liblog4cxx.pc

