program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -ma -qmaxmem=-1 -qarch=ppc" CC_LD_RT="-Wl,-blibpath:" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b" ;; *-hpux*) # +Onolimit causes test suite failure CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=c99 CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -msym -arch ev5 -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.[78]) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_NCURSES+-I${SB_VAR_NCURSES_INC}} \ ${SB_VAR_READLINE+-I${SB_VAR_READLINE_INC}} \ ${CPPFLAGS+${CPPFLAGS}}" \ LDFLAGS="${SB_VAR_NCURSES+-L${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_READLINE+-L${SB_VAR_READLINE_LIB}} \ ${SB_VAR_NCURSES+${CC_LD_RT}${SB_VAR_NCURSES_LIB}} \ ${SB_VAR_READLINE+${CC_LD_RT}${SB_VAR_READLINE_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ --enable-static --enable-shared --disable-dependency-tracking \ --datadir=${_datadir} --sysconfdir=${_sysconfdir} \ --libdir=${SB_INSTALL_PREFIX_LIB} \ --mandir="${SB_INSTALL_PREFIX}/man" ${ARGS+${ARGS}} \ --prefix=${SB_INSTALL_PREFIX}
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" lua.pc
