program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qlanglvl=extc99 -qro -qroconst -qmaxmem=-1 \ -qarch=ppc" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" LDFLAGS="-Wl,+nodefaultrpath" ;; *-hpux11.[01]*) CC=cc CFLAGS="+O2 -z +Ofltacc +Olit=all +DAportable \ +Oentrysched +Odataprefetch +Onolimit" ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" ;; esac
${SHELL} ./configure CC="${CC:=gcc}" CFLAGS="${CFLAGS:=-O2}" \ LDFLAGS="${LDFLAGS+${LDFLAGS}}" --mandir="${SB_INSTALL_PREFIX}/man" \ --infodir="${SB_INSTALL_PREFIX}/info" \ AWK="${SB_VAR_GAWK}/bin/gawk" \ --program-prefix=g \ --disable-dependency-tracking ${ARGS+${ARGS}} \ --prefix=${SB_INSTALL_PREFIX}
(rm -f doc/m4.info*) 2>/dev/null || :
