module.configure:
export PATH="${SB_VAR_FPING}/sbin:$PATH"
${SHELL} ./configure CC="${CC-gcc}" CFLAGS="${CFLAGS--O2}" \ CPPFLAGS="-I${SB_VAR_OPENLDAP_INC} \ ${CPPFLAGS+${CPPFLAGS}}" \ LDFLAGS="-L${SB_VAR_OPENLDAP_LIB} ${CC_LD_RT}${SB_VAR_OPENLDAP_LIB}:\ ${SB_VAR_LIBOPENSSL_LIB}:${SB_VAR_PGSQL_LIB}:${SB_VAR_MYSQL_LIB} \ ${LDFLAGS+${LDFLAGS}}" \ --with-nagios-user=nagios --with-nagios-group=nagios \ ${SB_VAR_GETTEXT+--with-libintl-prefix=${SB_VAR_GETTEXT}} \ ${SB_VAR_LIBICONV+--with-libiconv-prefix=${SB_VAR_LIBICONV}} \ --sysconfdir=${_sysconfdir} --localstatedir=${_localstatedir} \ --with-openssl=${SB_VAR_LIBOPENSSL} --with-pgsql=${SB_VAR_PGSQL} \ --with-mysql=${SB_VAR_MYSQL} --disable-dependency-tracking \ --prefix=${SB_INSTALL_PREFIX}
gmake configure

module.install:
${SB_PATH_INSTALL} -d -m 755 -o nagios -g nagios ${_sysconfdir}

gmake install

${SB_PATH_INSTALL} -m 644 -o nagios -g nagios \
lib/nagios-plugin-paths.cnf ${_sysconfdir}
