program.install:
gmake install.bin PKGDIR=${SB_INSTALL_PREFIX} gmake install.lib PKGDIR=${SB_INSTALL_PREFIX} gmake install.hdr PKGDIR=${SB_INSTALL_PREFIX}
${SB_PATH_INSTALL} -d -m 755 ${SB_INSTALL_PREFIX}/man \ ${SB_INSTALL_PREFIX}/man/man1 ${SB_INSTALL_PREFIX}/man/man3 \ ${SB_INSTALL_PREFIX}/man/man5 (cd userguide for i in 1 3 5; do ${SB_PATH_INSTALL} -m 644 -o root -g 0 *.${i} \ ${SB_INSTALL_PREFIX}/man/man${i} done)
# Don't ship man packages for non-existent binaries for i in hpcdtoppm.1 pbmto4425.1 pbmtoln03.1 pbmtolps.1 \ pbmtopk.1 pcdovtoppm.1 pktopbm.1 pnmtojbig.1 \ ppmtopjxl.1 ppmsvgalib.1 vidtoppm.1 picttoppm.1 \ jbigtopnm.1 spottopgm.1; do rm -f ${SB_INSTALL_PREFIX}/man/man1/${i} done
