module.configure:
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CXX="${CXX:-g++}" CXXFLAGS="${CXXFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_LIBREADLINE+-I${SB_VAR_LIBREADLINE_INC}} \ ${SB_VAR_GETTEXT+-I${SB_VAR_GETTEXT_INC}} \ ${SB_VAR_LIBICONV+-I${SB_VAR_LIBICONV_INC}} \ ${CPPFLAGS+${CPPFLAGS}}" \ LDFLAGS="${SB_VAR_LIBREADLINE+-L${SB_VAR_LIBREADLINE_LIB} \ ${CC_LD_RT}${SB_VAR_LIBREADLINE_LIB}} \ ${SB_VAR_GCC_RT+-L${SB_VAR_GCC_RT_LIB} \ ${CC_LD_RT}${SB_VAR_GCC_RT_LIB}} \ ${SB_VAR_GETTEXT+-L${SB_VAR_GETTEXT_LIB} \ ${CC_LD_RT}${SB_VAR_GETTEXT_LIB}} \ ${SB_VAR_LIBICONV+-L${SB_VAR_LIBICONV_LIB} \ ${CC_LD_RT}${SB_VAR_LIBICONV_LIB}} \ ${LDFLAGS+${LDFLAGS}}" \ PKG_CONFIG_PATH="\ ${SB_VAR_LIBCLN+${_libcln-${SB_VAR_LIBCLN_LIB}}/pkgconfig}\ ${SB_VAR_LIBGLIB+:${_libglib-${SB_VAR_LIBGLIB_LIB}}/pkgconfig}\ ${SB_VAR_LIBXML+:${SB_VAR_LIBXML_LIB}/pkgconfig}\ ${SB_VAR_GETTEXT+:${SB_VAR_GETTEXT_LIB}/pkgconfig}\ ${SB_VAR_LIBICONV+:${SB_VAR_LIBICONV_LIB}/pkgconfig}" \ --datadir=${_datadir} --datarootdir=${_datadir} \ --disable-dependency-tracking --docdir=${_docdir} \ --enable-defs2doc ${ARGS+${ARGS}} \ --mandir=${SB_INSTALL_PREFIX}/man \ --infodir=${SB_INSTALL_PREFIX}/info \ --libdir=${SB_INSTALL_PREFIX_LIB} \ --prefix=${SB_INSTALL_PREFIX}
gmake configure
${SB_PATH_SED} -i -e "\ s!-L\(\${libdir}\)!-L\1 ${CC_LD_RT}\1!;" libqalculate.pc
