program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" CC_LD_RT="-Wl,-blibpath:" ;; ia64*-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+b," ;; *-hpux11.[01]*) CC=cc CFLAGS="+O2 -Ae -z +ESlit +Ofltacc +DAportable \ +Oentrysched +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-hpux11*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" CC_LD_RT="-Wl,+s,+b," ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" CC_LD_RT="-Wl,-rpath," ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" CC_LD_RT="-Wl,-rpath," ;; *-linux*) CC_LD_RT="-Wl,-rpath," ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" CC_LD_RT="-Wl,-rpath," ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" CC_LD_RT="-R" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" CC_LD_RT="-R" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" CC_LD_RT="-R" ;; esac
rm -f config.cache CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_GETTEXT+-I${SB_VAR_GETTEXT_INC}}" \ LDFLAGS="${SB_VAR_GETTEXT+-L${SB_VAR_GETTEXT_LIB} \ ${CC_LD_RT}${SB_VAR_GETTEXT_LIB}} \ ${LDFLAGS+${LDFLAGS}}" PERL=${SB_VAR_PERL}/bin/perl \ PYTHON=${SB_VAR_PYTHON}/bin/python ./configure \ --without-included-gettext --enable-nls \ --with-locale-dir="${_datadir}/locale" \ --disable-dependency-tracking \ --libdir=${SB_INSTALL_PREFIX_LIB} \ --infodir="${SB_INSTALL_PREFIX}/info" \ --mandir="${SB_INSTALL_PREFIX}/man" --prefix=${SB_INSTALL_PREFIX}
CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="${SB_VAR_GETTEXT+-I${SB_VAR_GETTEXT_INC}}" \ LDFLAGS="${SB_VAR_GETTEXT+-L${SB_VAR_GETTEXT_LIB} \ ${CC_LD_RT}${SB_VAR_GETTEXT_LIB}} \ ${LDFLAGS+${LDFLAGS}}" PERL=${SB_VAR_PERL}/bin/perl \ PYTHON=${SB_VAR_PYTHON}/bin/python gmake configure

program.install:
gmake install

${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_docdir}
${SB_PATH_INSTALL} -m 644 -o root -g 0 doc/libiconv.texi \
doc/rfc1345.texi doc/recode.texi doc/version.texi ${_docdir}
