program.script-header:
_datadir="${SB_INSTALL_PREFIX}/share" _sysconfdir="${SB_INSTALL_PREFIX}/etc"
# Location of tclConfig.sh and tkConfig.sh PATH="${SB_VAR_TCL+$SB_VAR_TCL_LIB:}${SB_VAR_TK+$SB_VAR_TK_LIB:}$PATH"
test set = "${SB_VAR_LIBFFI+set}" && { export PKG_CONFIG_PATH="${SB_VAR_LIBFFI_LIB}/pkgconfig" }

program.build:
case "${SB_SYSTYPE}" in
*-aix*)
  export LIBPATH=${SB_BUILD_PREFIX} ;;
hppa*-hpux*)
  export SHLIB_PATH=${SB_BUILD_PREFIX} ;;
*)
  export LD_LIBRARY_PATH=${SB_BUILD_PREFIX} ;;
esac

gmake

program.test:
case "${SB_SYSTYPE}" in
*-aix6*)
  # Remove hanging test
  #rm -f bootstraptest/test_io.rb
  export LIBPATH=${SB_BUILD_PREFIX} ;;
*-aix*)
  export LIBPATH=${SB_BUILD_PREFIX} ;;
hppa*-hpux*)
  export SHLIB_PATH=${SB_BUILD_PREFIX} ;;
*-irix*)
  # Remove hanging tests
  rm -f bootstraptest/test_autoload.rb bootstraptest/test_exception.rb ;;
*-osf*)
  # Remove hanging test
  rm -f bootstraptest/test_autoload.rb ;;
*)
  export LD_LIBRARY_PATH=${SB_BUILD_PREFIX} ;;
esac

gmake test

:
