program.configure:
case "${SB_SYSTYPE}" in *-aix*) CC=xlc CFLAGS="-O2 -qro -qroconst -qmaxmem=-1 -qarch=ppc" LDFLAGS="-Wl,-brtl -Wl,-blibpath:/usr/lib" ;; *-hpux11.[01]*) CC=cc CFLAGS="-Ae -z +O2 +Ofltacc +ESlit +DAportable \ +Oentrysched +Odataprefetch +Onolimit" ;; *-hpux*) CC=cc CFLAGS="-z +O2 +Ofltacc +Olit=all +Oentrysched \ +Odataprefetch +Onolimit" ;; *-irix*) CC=cc CFLAGS="-Wl,-woff,84 -Wl,-woff,85 -woff 1009,1429 \ -use_readonly_const -G0 -rdata_shared -DEBUG:optimize_space=ON \ -O2 -OPT:Olimit=0" ;; *-freebsd*) CFLAGS="-O2 -mtune=pentium4 -march=pentium4" ;; i?86*-linux*) CFLAGS="-O2 -mtune=pentiumpro -march=pentiumpro" ;; *-osf*) CC=cc CFLAGS="-O2 -arch ev5 -msym -readonly_strings" ;; i?86*-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=pentium4" ;; *-solaris2.8) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -xarch=v8plusa" ;; *-solaris*) CC=cc CFLAGS="-mr -Qn -xstrconst -xO2 -xtarget=ultra2 \ -m32 -xarch=sparcvis" ;; esac
${SHELL} ./configure CC="${CC:-gcc}" CFLAGS="${CFLAGS:--O2}" \ CPPFLAGS="-I${SB_VAR_GETTEXT_INC}" LDFLAGS="${LDFLAGS+${LDFLAGS}}" \ ${SB_VAR_GETTEXT+--with-libintl-prefix=${SB_VAR_GETTEXT}} \ ${SB_VAR_LIBICONV+--with-libiconv-prefix=${SB_VAR_LIBICONV}} \ --program-prefix=g --disable-dependency-tracking \ --datarootdir=${_datadir} --datadir=${_datadir} \ --docdir=${_docdir} --infodir="${SB_INSTALL_PREFIX}/info" \ --mandir="${SB_INSTALL_PREFIX}/man" --prefix=${SB_INSTALL_PREFIX}
(rm -f doc/sed.info*) 2>/dev/null || :
