program.install:
_pyexe=python [ -x ${SB_VAR_PYTHON}/bin/python3 ] && _pyexe=python3
PYTHONPATH="${SB_VAR_DOCUTILS}/lib/${_pysubdir}:\ ${SB_VAR_JINJA}/lib/${_pysubdir}:\ ${SB_VAR_JINJA}/lib/${_pysubdir}/jinja2:\ ${SB_VAR_PYGMENTS}/lib/${_pysubdir}:\ ${SB_VAR_SETUPTOOLS}/lib/${_pysubdir}" \ ${SB_VAR_PYTHON}/bin/${_pyexe} setup.py install -O1 \ --skip-build --single-version-externally-managed --root / \ --install-lib=${_install_prefix} \ --prefix=${_binman_prefix-${SB_INSTALL_PREFIX}}
# replace binary and install documentation and man pages if [ "${_binman_prefix+set}" != set ]; then for _bin in sphinx-autogen.py sphinx-build.py \ sphinx-quickstart.py; do ${SB_PATH_INSTALL} -m 755 -o root -g 0 ${_bin} \ ${SB_INSTALL_PREFIX}/bin/${_bin%%.py} ${SB_PATH_SED} -i -e "\ 1s|^.*|#!${SB_VAR_PYTHON}/bin/${_pyexe}|;\ s!@docutils@!${SB_VAR_DOCUTILS}/lib/${_pysubdir}!;\ s!@jinja@!${SB_VAR_JINJA}/lib/${_pysubdir}!;\ s!@libdir@!${_install_prefix}!;" \ ${SB_INSTALL_PREFIX}/bin/${_bin%%.py} done
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d ${_docdir} (cd doc ${SB_INSTALL_PREFIX}/bin/sphinx-build -d _build/doctrees \ . ${_docdir}/html) rm -rf ${_docdir}/html/_sources ${_docdir}/html/.buildinfo ${SB_PATH_FIND} ${_docdir} -type d -exec ${SB_PATH_CHMOD} 755 {} \; ${SB_PATH_FIND} ${_docdir} -type f -exec ${SB_PATH_CHMOD} 644 {} \;
${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \ ${SB_INSTALL_PREFIX}/man/man1 for _man in sphinx-autogen.1 sphinx-build.1 sphinx-quickstart.1; do ${SB_PATH_INSTALL} -m 644 -o root -g 0 ${_man} \ ${SB_INSTALL_PREFIX}/man/man1/${_man} ${SB_PATH_SED} -i -e "\ s!/usr/share/doc/python-sphinx!${_docdir}!;" \ ${SB_INSTALL_PREFIX}/man/man1/${_man} done fi
# install path for Python's include.paths.d ${SB_PATH_INSTALL} -m 755 -o root -g 0 -d \ ${_install_prefix}/include.paths.d cat >${_install_prefix}/include.paths.d/99-${SB_PROG_NAME} <<_EOT_ ${_install_prefix} _EOT_

